import React from 'react';

const lockCombo = ["1", "2", "3"];
const success = "Success! You've opened lock number 1!";
const failure = "Please try again!";



class Card extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            numsGuessed: []
        }
    }

    reset = () => {
        this.setState.numsGuessed = [];
        document.querySelector(".numGuessed1").innerHTML = "";
        document.querySelector(".numGuessed2").innerHTML = "";
        document.querySelector(".numGuessed3").innerHTML = "";
    }

    componentDidMount() {
        document.onkeydown = function(event) {
            let number = event.key;

            // this.numsGuessed.push(number);
            this.setState(this.numsGuessed);

            if (number === lockCombo[0]) {
                document.querySelector(".numGuessed1").innerHTML = number;
            } else if (number === lockCombo[1]) {
                document.querySelector(".numGuessed2").innerHTML = number;
            } else if (number === lockCombo[2]) {
                document.querySelector(".numGuessed3").innerHTML = number;
            } else {
                alert(failure);
                // reset();
            }

            if (this.numsGuessed[0] === "1" && this.numsGuessed[1] === "2" && this.numsGuessed[2] === "3") {
                alert(success);
            }
        }
    }

    render() {
        return (
           <div>
                <div className="bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5">
                    <div>
                        <p className="numGuessed1"></p>
                    </div>
                </div>
                <div className="bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5">
                    <div>
                        <p className="numGuessed2"></p>
                    </div>
                </div>
                <div className="bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5">
                    <div>
                        <p className="numGuessed3"></p>
                    </div>
                </div>
           </div>
        );
    }
}

export default Card;
